/// Look up an interface type name by its IANA ifType number.
/// Returns the symbolic name if found, or None if the number is not in the registry.
///
/// # Example
/// ```
/// use scanopy::server::snmp_credentials::resolution::generated::get_if_type_name;
///
/// assert_eq!(get_if_type_name(6), Some("ethernetCsmacd"));
/// assert_eq!(get_if_type_name(117), Some("gigabitEthernet"));
/// assert_eq!(get_if_type_name(999), None);
/// ```
pub fn get_if_type_name(if_type: i32) -> Option<&'static str> {
    IF_TYPES.get(&if_type).copied()
}

/// Get a human-friendly description for an interface type.
/// Returns a more readable version of the type name for common interfaces.
#[allow(dead_code)] // Used by SNMP collection module
pub fn get_if_type_description(if_type: i32) -> &'static str {
    match if_type {
        1 => "Other",
        6 => "Ethernet",
        24 => "Software Loopback",
        53 => "Virtual Interface",
        62 => "Fast Ethernet",
        71 => "Wi-Fi (802.11)",
        117 => "Gigabit Ethernet",
        131 => "Tunnel",
        135 => "VLAN (L2)",
        136 => "VLAN (L3 IPv4)",
        161 => "Link Aggregation (802.3ad)",
        166 => "MPLS",
        209 => "Bridge",
        _ => get_if_type_name(if_type).unwrap_or("Unknown"),
    }
}

